export default {
	sendMessages:() =>{
		if(DiscordSwitch.isSwitchedOn){
		 DiscordApi.run()
			const successMessage1 = "Discord message sent to ".concat(List1.selectedItem.MailingList).concat("server")
			showAlert(successMessage1,"success")
		}
		if(EmailSwitch.isSwitchedOn){
			SendEmail.run()
			const successMessage2 = "Email sent to ".concat(List1.selectedItem.MailingList).concat("list")
			showAlert(successMessage2,"success")
		}
		resetWidget('Message_Modal')
		closeModal("Message_Modal")
	},
	getEmails: () =>{
		let nameArray = Table2.selectedRows.map(el=> el.CustomersEmailID)
		return nameArray.toString()
	},
	dndList: ()=>{
		let dndCount = 0
		let reachable = 0
		getAllCustomersList.data.forEach(a => a.UsersDoNotDisturb==0?dndCount++:reachable++)
		const ret = [{"x":"Reachable","y":dndCount},{"x":"Do not Disturb", "y":reachable}]
		return ret

	}
}
